mapper.select:
select t_property.* from t_favorite
       LEFT JOIN t_property on t_favorite.property_id = t_property.property_id
       where t_favorite.user_id = #{userId}

select t_property.* from t_favorite
       LEFT JOIN t_property on t_favorite.property_id = t_property.property_id
       where t_favorite.user_id = #{userId} and t_property.title LIKE #{likeTitle}
